system_instruction:
"messages": [

            {"role": "user", "content": f"/nothink {query}"}
        ],
        'tools':[
      {
      "type": "function",
      "function": {
  "name": "query_work_order_data",
  "description": "根据指定条件查询工单数据",
  "parameters": {
    "type": "object",
    "required": [
      "data_query_type",
      "order_type",
      "region_name",
      "county_name",
      "order_status",
      "date_type"
    ],
    "properties": {
      "data_query_type": {
        "type": "string",
        "description": "固定为 工单信息"
      },
      "order_type": {
        "type": "integer",
        "enum": [1, 2, 3],
        "description": "工单类型：1-售中开通工单，2-投诉工单，3-故障工单"
      },
      "region_name": {
        "type": "string",
        "description": "地市名称"
      },
      "county_name": {
        "type": "string",
        "description": "区县名称"
      },
      "order_status": {
        "type": "string",
        "enum": ["进行中", "已完成"],
        "description": "工单状态"
      },
      "date_type": {
        "type": "integer",
        "enum": [1, 2, 3],
        "description": "时间类型：1-当前在途工单，2-按派单时间，3-按报结时间"
      },
      "start_time": {
        "type": "string",
        "format": "date-time",
        "description": "开始时间，格式：YYYY-MM-DD HH:mm:ss。当 date_type 为 1 时可不填，其他情况必填",
        "pattern": "^\\d{4}-\\d{2}-\\d{2} \\d{2}:\\d{2}:\\d{2}$"
      },
      "end_time": {
        "type": "string",
        "format": "date-time",
        "description": "结束时间，格式：YYYY-MM-DD HH:mm:ss。当 date_type 为 1 时可不填，其他情况必填",
        "pattern": "^\\d{4}-\\d{2}-\\d{2} \\d{2}:\\d{2}:\\d{2}$"
      }
    },
    "additionalProperties": False
  }
